import React, {
  useState,
  useEffect,
  // Suspense
} from "react"
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"
import io from "socket.io-client"
import "./App.css"
import { MDBContainer } from "mdb-react-ui-kit"
import Navbar from "./components/navbar"
// import Breadcrumb from "./components/breadcrumb"
import WheelOfFortune from "./components/wheelOfFortune/"

const socket = io({
  path: "/socket",
})

const routeItems = [
  { route: "/", text: "Home" },
  { route: "/games", text: "Games" },
]

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected)
  // const [lastPong, setLastPong] = useState(null)

  useEffect(() => {
    socket.on("connect", async () => {
      setIsConnected(true)
      console.log("socket connected")
    })

    socket.on("disconnect", () => {
      setIsConnected(false)
    })

    // socket.on("pong", () => {
    //   setLastPong(new Date().toISOString())
    // })

    return () => {
      socket.off("connect")
      socket.off("disconnect")
      // socket.off("pong")
    }
  }, [])

  return (
    <Router>
      {!isConnected ? (
        <div className="text-center">Socket connecting...</div>
      ) : (
        <div className="">
          <Navbar routeItems={routeItems} />
          {/* <Breadcrumb /> */}
          <MDBContainer className="vh-100 d-flex justify-content-center align-items-center">
            <Routes>
              <Route end path="/" element={<h5>Home</h5>}></Route>
              <Route end path="/games" element={<h5>Games</h5>}></Route>
              <Route
                end
                path="/games/wheel-of-fortune"
                element={WheelOfFortune}
              ></Route>
            </Routes>
          </MDBContainer>
        </div>
      )}
    </Router>
  )
}

export default App
