import React, {
  useState,
  useEffect,
  // Suspense
} from "react"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  NavLink,
  Outlet,
} from "react-router-dom"
import io from "socket.io-client"
import "./App.css"
import { MDBContainer } from "mdb-react-ui-kit"

const socket = io({
  path: "/socket",
})

const routeItems = [
  { route: "/", text: "Home" },
  { route: "/games", text: "Games" },
]

const Home = () => {
  return (
    <div>
      <Link to="/games">Games</Link>
    </div>
  )
}
const Games = () => {
  return (
    <div>
      <Link to="/games/wheel-of-fortune">Wheel of Fortune</Link>
      <Outlet />
    </div>
  )
}

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected)
  // const [lastPong, setLastPong] = useState(null)

  useEffect(() => {
    socket.on("connect", async () => {
      setIsConnected(true)
      console.log("socket connected")
    })

    socket.on("disconnect", () => {
      setIsConnected(false)
    })

    // socket.on("pong", () => {
    //   setLastPong(new Date().toISOString())
    // })

    return () => {
      socket.off("connect")
      socket.off("disconnect")
      // socket.off("pong")
    }
  }, [])

  return <MDBContainer className="pt-4 vh-100 text-left"></MDBContainer>
}

export default App
