import React, { Suspense } from "react"
import ReactDOM from "react-dom"
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Outlet,
  useOutletContext,
  Link,
} from "react-router-dom"
import * as serviceWorker from "./serviceWorker"
import { ToastContainer, toast } from "react-toastify"
import { MDBContainer } from "mdb-react-ui-kit"
import "mdb-react-ui-kit/dist/css/mdb.min.css"
import "@fortawesome/fontawesome-free/css/all.min.css"
import "./index.css"
import "./libs/css/gradientBg.css"
import "./libs/css/Animate.min.css"
import HomePage from "./components/homePage"
import Footer from "./components/footer"

const WheelMng = React.lazy(() => import("./App"))
const WidgetPage = React.lazy(() => import("./widgetPage"))

window.toast = toast

window.wait = async (t = 1) => {
  return new Promise((resolve) => setTimeout(() => resolve, t * 1000))
}

window.imageUrlFixing = (url = "") => {
  return url.replace("p16-sign-va.tiktokcdn.com", "p16-va.tiktokcdn.com")
}

window.randomInt = (min = 10, max = 99) =>
  Math.floor(Math.random() * (max - min + 1) + min)

const Loading = () => {
  return <></>
}

// const HomePage = () => {
//   return (
//     <ul>
//       Tiktok Livestream Game
//       <li className="fw-bold">
//         <Link to="/wheel-of-fortune">Wheel of Fortune</Link>
//       </li>
//     </ul>
//   )
// }

const MngWheelGame = () => (
  <Suspense fallback={<Loading />}>
    <WheelMng />
  </Suspense>
)

const WheelGame = React.lazy(() => import("./Game"))
const WidgetWheelOfFortune = () => {
  const [event, settings] = useOutletContext()
  return (
    <Suspense fallback={<Loading />}>
      <WheelGame event={event} settings={settings} />
    </Suspense>
  )
}

const GeneralLayout = () => {
  return (
    <>
      <MDBContainer
        style={{
          paddingTop: "30px",
        }}
      >
        <div style={{ minHeight: "85vh" }}>
          <Outlet />
        </div>
        <div style={{ height: "15vh" }}>
          <Footer />
        </div>
      </MDBContainer>

      <ToastContainer
        theme="light"
        position="bottom-right"
        toastStyle={{
          fontSize: "14px",
          textAlign: "left",
        }}
        hideProgressBar={false}
      />
      <div id="gradient_bg"></div>
    </>
  )
}

const Main = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<GeneralLayout />}>
          <Route index element={<HomePage />} />
          <Route path="wheel-of-fortune" element={<MngWheelGame />} />
        </Route>
        <Route
          path="widget"
          element={
            <Suspense fallback={<Loading />}>
              <WidgetPage />
            </Suspense>
          }
        >
          <Route path="wheel-of-fortune" element={<WidgetWheelOfFortune />} />
        </Route>
        <Route
          path="*"
          element={
            <div className="text-center">
              <h5>ERROR! Page not found</h5>
            </div>
          }
        />
      </Routes>
    </Router>
  )
}

ReactDOM.render(<Main />, document.getElementById("root"))

serviceWorker.unregister()
