import React, {
  useState,
  useEffect,
  // Suspense,
  Link,
  NavLink,
} from "react"
import { BrowserRouter as Router, Routes, Route } from "react-router-dom"
import io from "socket.io-client"
import "./App.css"
import { MDBContainer } from "mdb-react-ui-kit"
import Navbar from "./components/navbar"
// import Breadcrumb from "./components/breadcrumb"
import WheelOfFortune from "./components/wheelOfFortune/"

const socket = io({
  path: "/socket",
})

const routeItems = [
  { route: "/", text: "Home" },
  { route: "/games", text: "Games" },
]

const Game = () => {
  return (
    <div>
      <h5>Games</h5>
      <ul>
        <li>
          <Link
            // className="nav-link"
            to="/games/wheel-of-fortune"
          >
            Wheel of Fortune
          </Link>
          {/* <Link to="/games/wheel-of-fortune">Wheel of Fortune</Link> */}
          {/* <a href="/games/wheel-of-fortune">Wheel of Fortune</a> */}
        </li>
      </ul>
    </div>
  )
}

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected)
  // const [lastPong, setLastPong] = useState(null)

  useEffect(() => {
    socket.on("connect", async () => {
      setIsConnected(true)
      console.log("socket connected")
    })

    socket.on("disconnect", () => {
      setIsConnected(false)
    })

    // socket.on("pong", () => {
    //   setLastPong(new Date().toISOString())
    // })

    return () => {
      socket.off("connect")
      socket.off("disconnect")
      // socket.off("pong")
    }
  }, [])

  return (
    <Router>
      {!isConnected ? (
        <div className="text-center">Socket connecting...</div>
      ) : (
        <>
          <Navbar routeItems={routeItems} />
          {/* <Breadcrumb /> */}
          <MDBContainer className="pt-4 vh-100 text-left">
            <Routes>
              <Route end path="/" element={<h5>Home</h5>}></Route>
              <Route end path="/games" element={<Game />}></Route>
              <Route
                end
                path="/games/wheel-of-fortune"
                element={<WheelOfFortune />}
              ></Route>
            </Routes>
          </MDBContainer>
        </>
      )}
    </Router>
  )
}

export default App
