import React, { useState, useEffect, useRef } from "react"

import io from "socket.io-client"
import "react-toastify/dist/ReactToastify.css"
import "./App.css"
import { ToastContainer, toast } from "react-toastify"
import {
  MDBContainer,
  MDBInputGroup,
  MDBBtn,
  MDBIcon,
  MDBRow,
  MDBCol,
  MDBCheckbox,
  MDBRange,
  MDBRadio,
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
} from "mdb-react-ui-kit"
import Wheel from "./Widget"

const socket = io({
  path: "/socket",
})

const defaultSettings = JSON.stringify({
  commentKey: "join",
  follower: true,
  friend: true,
  giftCount: 1,
  giftName: "rose",
  joinEvent: "chat",
  maxPlayer: 100,
  unfollower: true,
  winnerShowDuration: 10,
})

const LoadingPage = () => {
  return <h5>connecting...</h5>
}

const Options = ({ current, update }) => {
  function handleChange(e) {
    const target = e.target
    const type = target.type
    const name = target.name
    var value = target.value
    switch (type) {
      case "radio":
        if ("true false".includes(value)) {
          value = value === "true"
        }
        break
      case "checkbox":
        value = target.checked
        break
      case "number":
      case "range":
        const min = parseInt(target.min)
        const int = parseInt(value || min)
        const float = parseFloat(value || min)
        value = Math.max(min, int, float)
        target.value = value
        break
      case "text":
        break
      default:
    }
    console.log(name, value)
    return update((current) => ({ ...current, [name]: value }))
  }
  return (
    <MDBCard>
      <MDBCardHeader>
        <h5>Setting</h5>
      </MDBCardHeader>
      <MDBCardBody>
        <MDBRow className="mb-3">
          <MDBCol size="md" md="4" lg="5">
            <label>Người chơi</label>
          </MDBCol>
          <MDBCol size="md" md="8" lg="7">
            <MDBCheckbox
              label="Bạn bè"
              name="friend"
              defaultChecked={current["friend"]}
              onChange={handleChange}
            />
            <MDBCheckbox
              label="Người theo dõi"
              name="follower"
              defaultChecked={current["follower"]}
              onChange={handleChange}
            />
            <MDBCheckbox
              label="Người chưa theo dõi"
              name="unfollower"
              defaultChecked={current["unfollower"]}
              onChange={handleChange}
            />
          </MDBCol>
        </MDBRow>
        <MDBRow className="mb-3">
          <MDBCol size="md" md="4" lg="5">
            <label>Số lượng người chơi</label>
          </MDBCol>
          <MDBCol size="md" md="8" lg="7">
            <MDBRange
              name="maxPlayer"
              defaultValue={current["maxPlayer"]}
              onChange={handleChange}
              step="1"
              min="2"
              max="100"
              type="range"
              className="form-range"
            />
          </MDBCol>
        </MDBRow>
        <MDBRow className="mb-3">
          <MDBCol size="md" md="4" lg="5">
            <label>Hành động</label>
          </MDBCol>
          <MDBCol size="md" lg="7" className="d-flex align-items-center gap-3">
            <MDBRadio
              name="joinEvent"
              label="Comment"
              value="chat"
              defaultChecked={current["joinEvent"] === "chat"}
              onChange={handleChange}
            />
            <MDBRadio
              name="joinEvent"
              label="Tặng quà"
              value="gift"
              defaultChecked={current["joinEvent"] === "gift"}
              onChange={handleChange}
            />
          </MDBCol>
        </MDBRow>
        {current["joinEvent"] === "chat" && (
          <MDBRow className="mb-3">
            <MDBCol size="md" md="4" lg="5">
              <label>Từ khoá comment</label>
            </MDBCol>
            <MDBCol size="md" md="8" lg="7">
              <input
                name="commentKey"
                defaultValue={current["commentKey"]}
                onInput={handleChange}
                type="text"
                className="form-control"
              />
            </MDBCol>
          </MDBRow>
        )}
        {current["joinEvent"] === "gift" && (
          <MDBRow className="mb-3">
            <MDBCol size="md" md="4" lg="5">
              <label>Quà tặng</label>
            </MDBCol>
            <MDBCol
              size="md"
              md="8"
              lg="5"
              xl="4"
              className="d-flex align-items-center"
            >
              <MDBInputGroup>
                <input
                  name="giftCount"
                  defaultValue={current["giftCount"]}
                  onInput={handleChange}
                  className="form-control "
                  type="number"
                  min="1"
                  step="1"
                />
                <select
                  name="giftName"
                  defaultValue={current["giftName"]}
                  onInput={handleChange}
                  className="form-control w-75"
                >
                  <option value="rose">Hoa Hồng</option>
                  <option value="tiktok">Tiktok</option>
                </select>
              </MDBInputGroup>
            </MDBCol>
          </MDBRow>
        )}
        <MDBRow className="mb-3">
          <MDBCol size="md" md="4" lg="5">
            <label>
              Thời gian hiện người thắng <small>(giây)</small>
            </label>
          </MDBCol>
          <MDBCol size="md" md="8" lg="7">
            <MDBRange
              name="winnerShowDuration"
              defaultValue={current["winnerShowDuration"] || 5}
              onChange={handleChange}
              step="5"
              min="5"
              max="100"
              type="range"
              className="form-range"
            />
          </MDBCol>
        </MDBRow>
      </MDBCardBody>
    </MDBCard>
  )
}

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected)
  const [event, updateEvent] = useState({})
  const [settings, updateSettings] = useState(
    JSON.parse(window.localStorage.settings || defaultSettings)
  )
  const tiktokId = useRef(null)
  const startBtn = useRef(null)

  function start() {
    tiktokId.current.setAttribute("disabled", true)
    startBtn.current.setAttribute("disabled", true)
    const id = tiktokId.current.value
    socket.emit("createRoom", id)
  }
  useEffect(() => {
    socket.on("connect", async () => {
      setIsConnected(true)
      console.log("socket connected")
    })

    socket.on("disconnect", () => {
      setIsConnected(false)
    })

    socket.on("tiktok-chat", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-gift", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-follow", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-share", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-like", (data) => {
      updateEvent(data)
    })

    return () => {
      socket.off("connect")
      socket.off("disconnect")
    }
  }, [])

  useEffect(() => {
    tiktokId.current.focus()

    socket.on("tiktok-connectSuccess", () => {
      toast.success(`connect success`)
    })

    socket.on("tiktok-connectFailed", (error) => {
      tiktokId.current.removeAttribute("disabled")
      startBtn.current.removeAttribute("disabled")
      toast.error(`connect failed - ${error}`)
    })

    socket.on("tiktok-streamEnd", (reason) => {
      tiktokId.current.removeAttribute("disabled")
      startBtn.current.removeAttribute("disabled")
      toast.info(`livestream ended - ${reason}`)
    })

    return () => {
      socket.off("tiktok-connectSuccess")
      socket.off("tiktok-connectFailed")
      socket.off("tiktok-streamEnd")
    }
  }, [])

  useEffect(() => {
    window.localStorage.setItem("settings", JSON.stringify(settings))
    clearTimeout(window.settingsTimeout)
    window.settingsTimeout = setTimeout(
      () => socket.emit("updateSettings", settings),
      1000
    )
  }, [settings])

  useEffect(() => {
    console.log(event.name, event.uniqueId)
  }, [event])

  return (
    <MDBContainer className="pt-4 vh-100 text-left">
      {!isConnected ? (
        <LoadingPage />
      ) : (
        <>
          <h5>Wheel of Fortune</h5>
          <span>{event.name}</span>
          <MDBRow className="mb-3">
            <MDBCol size="md" md="9" lg="8" xl="6">
              <Options current={settings} update={updateSettings} />
            </MDBCol>
          </MDBRow>

          <MDBInputGroup className="mb-3" style={{ maxWidth: "300px" }}>
            <input
              ref={tiktokId}
              className="form-control"
              placeholder="enter tiktok id"
              type="text"
              defaultValue="dienbo94"
            />
            <MDBBtn ref={startBtn} onClick={start}>
              Start <MDBIcon fas icon="angle-right" />
            </MDBBtn>
          </MDBInputGroup>

          <ToastContainer
            theme="light"
            position="bottom-right"
            toastStyle={{
              fontSize: "14px",
              textAlign: "left",
            }}
            hideProgressBar={false}
          />
          <Wheel settings={settings} event={event} isDemo={true} />
        </>
      )}
      <ToastContainer
        theme="light"
        position="bottom-right"
        toastStyle={{
          fontSize: "14px",
          textAlign: "left",
        }}
        hideProgressBar={false}
      />
    </MDBContainer>
  )
}

export default App
