function WheelofFortune({ socketId }) {
  const [isConnected, setIsConnected] = useState(false)
  const [socket, setSocket] = useState(
    io("/widget", {
      path: "/socket",
      query: {
        cid: new URLSearchParams(document.location.search).get("cid"),
      },
    })
  )
  const [event, updateEvent] = useState({})
  const [settings, updateSettings] = useState(defaultSettings)
  const [players, setPlayers] = useState({})
  const [canJoin, setCanjoin] = useState(true)
  const [winner, setWinner] = useState(false)
  const [isSpining, setIsSpining] = useState(false)

  function startSpin() {
    if (Object.keys(players).length < 2 || isSpining) return
    window.wheel.rotationAngle = Math.floor(Math.random() * 359) + 1
    window.wheel.draw()
    window.wheel.startAnimation()
    setCanjoin(false)
    setWinner(false)
    setIsSpining(true)
  }

  function resetWheel() {
    try {
      window.wheel.stopAnimation(false)
    } catch (e) {}
    window.wheel.rotationAngle = 0
    window.wheel.draw()
    setPlayers({})
    setIsSpining(false)
    setCanjoin(true)
    setWinner(false)
  }

  const addListener = () => {
    socket.on("connect", async () => {
      setIsConnected(true)
      console.log("widget socket connected")
    })

    socket.on("disconnect", () => {
      setIsConnected(false)
      console.log("widget socket disconnected")
    })

    socket.on("tiktok-chat", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-gift", (data) => {
      updateEvent(data)
    })

    socket.on("updateSettings", (data) => {
      updateSettings(data)
    })
  }

  const removeListener = () => {
    socket.disconnect()
    socket.off("connect")
    socket.off("disconnect")
    socket.off("updateSettings")
    socket.off("tiktok-chat")
    socket.off("tiktok-gift")
  }

  useEffect(() => {
    const wsocket = setSocket(
      io("/widget", {
        path: "/socket",
        query: {
          cid: socketId,
        },
      })
    )

    addListener()

    return () => {}
  }, [socketId])

  useEffect(() => {
    const validFollowrRole = {
      0: settings.unfollower,
      1: settings.follower,
      2: settings.friend,
    }

    const { name, uniqueId, nickname, profilePictureUrl, followRole } = event

    if (name === "spin") return startSpin()
    if (name === "restart") return resetWheel()
    setPlayers((current) => {
      const isLimit = Object.keys(current).length >= settings.maxPlayer
      if (isLimit || !canJoin) return current

      const isJoined = current[uniqueId]
      if (
        isJoined ||
        name !== settings.joinEvent ||
        !validFollowrRole[followRole]
      )
        return current

      var valid
      switch (name) {
        case "chat":
          const { comment } = event
          valid = comment && comment.includes(settings.commentKey)
          break
        case "gift":
          const { giftType, giftName, repeatCount, repeatEnd } = event
          if (giftType === 1 && !repeatEnd) break
          valid =
            giftName === settings.giftName && repeatCount < settings.giftCount
          break
        default:
      }
      return valid
        ? { ...current, [uniqueId]: { uniqueId, nickname, profilePictureUrl } }
        : current
    })
  }, [event, canJoin, settings])

  useEffect(() => {
    window.wheelFinished = () => {
      let winningSegment = window.wheel.getIndicatedSegment()
      setWinner(winningSegment.id ? winningSegment : false)
      setIsSpining(false)
      console.warn(winningSegment.text, "is winner")
    }

    return () => {
      window.wheelFinished = null
    }
  }, [])

  return (
    <div
      className="position-relative mx-auto"
      style={{ width: `${size}px`, height: `${size}px` }}
    >
      <TutorialCard
        settings={settings}
        playerCount={Object.keys(players).length}
        show={canJoin}
      />
      <div
        className="position-absolute w-100 text-center"
        style={{ zIndex: "1" }}
      >
        <JoinedPlayer players={players} show={canJoin} />
      </div>
      <div className="position-absolute  ">
        <Wheel players={players} />
      </div>
      <div className="position-absolute w-100 h-100">
        <WheelBackground />
      </div>
      <div className="position-absolute w-100 h-100">
        {winner && winner.id && (
          <Winner info={winner} duration={settings.winnerShowDuration || 10} />
        )}
      </div>
    </div>
  )
}
