import React, { useState, useEffect, useRef } from "react"

import io from "socket.io-client"
import "react-toastify/dist/ReactToastify.css"
import "./App.css"
import { ToastContainer, toast } from "react-toastify"
import {
  MDBContainer,
  MDBInputGroup,
  MDBBtn,
  MDBIcon,
  MDBRow,
  MDBCol,
  MDBCheckbox,
  MDBRange,
  MDBRadio,
} from "mdb-react-ui-kit"

const socket = io({
  path: "/socket",
})

const LoadingPage = () => {
  return <h5>connecting...</h5>
}

const Options = () => {
  return (
    <div className=" ">
      <MDBRow className="mb-3">
        <MDBCol size="md" md="4">
          <label>Người chơi</label>
        </MDBCol>
        <MDBCol size="md" md="8">
          <MDBCheckbox
            label="Bạn bè"
            name={sName("friend")}
            defaultChecked={sValue("friend")}
            onChange={handleChange}
          />
          <MDBCheckbox
            label="Người theo dõi"
            name={sName("follower")}
            defaultChecked={sValue("follower")}
            onChange={handleChange}
          />
          <MDBCheckbox
            label="Người chưa theo dõi"
            name={sName("unfollower")}
            defaultChecked={sValue("unfollower")}
            onChange={handleChange}
          />
        </MDBCol>
      </MDBRow>
      <MDBRow className="mb-3">
        <MDBCol size="md" md="4">
          <label>Số lượng người chơi</label>
        </MDBCol>
        <MDBCol size="md" md="8">
          <MDBRange
            name={sName("maxPlayer")}
            defaultValue={sValue("maxPlayer")}
            onChange={handleChange}
            step="1"
            min="2"
            max="100"
            type="range"
            className="form-range"
          />
        </MDBCol>
      </MDBRow>
      <MDBRow className="mb-3">
        <MDBCol size="md" md="4">
          <label>Hành động</label>
        </MDBCol>
        <MDBCol size="md" md="8" className="d-flex align-items-center gap-3">
          <MDBRadio
            name={sName("joinEvent")}
            label="Comment"
            value="chat"
            defaultChecked={sValue("joinEvent") === "chat"}
            onChange={handleChange}
          />
          <MDBRadio
            name={sName("joinEvent")}
            label="Tặng quà"
            value="gift"
            defaultChecked={sValue("joinEvent") === "gift"}
            onChange={handleChange}
          />
        </MDBCol>
      </MDBRow>
      {sValue("joinEvent") === "chat" && (
        <MDBRow className="mb-3">
          <MDBCol size="md" md="4">
            <label>Từ khoá comment</label>
          </MDBCol>
          <MDBCol size="md" md="8">
            <input
              name={sName("commentKey")}
              defaultValue={sValue("commentKey")}
              onInput={handleChange}
              type="text"
              className="form-control"
            />
          </MDBCol>
        </MDBRow>
      )}
      {sValue("joinEvent") === "gift" && (
        <MDBRow className="mb-3">
          <MDBCol size="md" md="4">
            <label>Quà tặng</label>
          </MDBCol>
          <MDBCol size="md" md="8" className="d-flex align-items-center">
            <MDBInputGroup>
              <input
                name={sName("giftCount")}
                defaultValue={sValue("giftCount")}
                onInput={handleChange}
                className="form-control "
                type="number"
                min="1"
                step="1"
              />
              <select
                name={sName("giftName")}
                defaultValue={sValue("giftName")}
                onInput={handleChange}
                className="form-control w-75"
              >
                <option value="rose">Hoa Hồng</option>
                <option value="tiktok">Tiktok</option>
              </select>
            </MDBInputGroup>
          </MDBCol>
        </MDBRow>
      )}
      <MDBRow className="mb-3">
        <MDBCol size="md" md="4">
          <label>
            Thời gian hiện người thắng <small>(giây)</small>
          </label>
        </MDBCol>
        <MDBCol size="md" md="8">
          <MDBRange
            name={sName("winnerShowDuration")}
            defaultValue={sValue("winnerShowDuration") || 5}
            onChange={handleChange}
            step="5"
            min="5"
            max="100"
            type="range"
            className="form-range"
          />
        </MDBCol>
      </MDBRow>
    </div>
  )
}

const Controller = ({ event }) => {
  useEffect(() => {}, [event])
  const tiktokId = useRef(null)
  const startBtn = useRef(null)

  function start() {
    tiktokId.current.setAttribute("disabled", true)
    startBtn.current.setAttribute("disabled", true)
    const id = tiktokId.current.value
    socket.emit("createRoom", id)
  }

  useEffect(() => {
    tiktokId.current.focus()

    socket.on("tiktok-connectSuccess", () => {
      toast.success(`connect success`)
    })

    socket.on("tiktok-connectFailed", (error) => {
      tiktokId.current.removeAttribute("disabled")
      startBtn.current.removeAttribute("disabled")
      toast.error(`connect failed - ${error}`)
    })

    socket.on("tiktok-streamEnd", (reason) => {
      tiktokId.current.removeAttribute("disabled")
      startBtn.current.removeAttribute("disabled")
      toast.info(`livestream ended - ${reason}`)
    })

    return () => {
      socket.off("tiktok-connectSuccess")
      socket.off("tiktok-connectFailed")
      socket.off("tiktok-streamEnd")
    }
  }, [])

  return (
    <MDBContainer>
      <h5>controller</h5>
      <MDBInputGroup className="mb-3" style={{ maxWidth: "300px" }}>
        <input
          ref={tiktokId}
          className="form-control"
          placeholder="enter tiktok id"
          type="text"
          defaultValue="dienbo94"
        />
        <MDBBtn ref={startBtn} onClick={start}>
          Start <MDBIcon fas icon="angle-right" />
        </MDBBtn>
      </MDBInputGroup>
      <span>{event.name}</span>
      <ToastContainer
        theme="light"
        position="bottom-right"
        toastStyle={{
          fontSize: "14px",
          textAlign: "left",
        }}
        hideProgressBar={false}
      />
    </MDBContainer>
  )
}

function App() {
  const [isConnected, setIsConnected] = useState(socket.connected)
  const [event, updateEvent] = useState({})

  useEffect(() => {
    socket.on("connect", async () => {
      setIsConnected(true)
      console.log("socket connected")
    })

    socket.on("disconnect", () => {
      setIsConnected(false)
    })

    socket.on("tiktok-chat", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-gift", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-follow", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-share", (data) => {
      updateEvent(data)
    })

    socket.on("tiktok-like", (data) => {
      updateEvent(data)
    })

    return () => {
      socket.off("connect")
      socket.off("disconnect")
    }
  }, [])

  useEffect(() => {
    console.log(event.name, event.uniqueId)
  }, [event])

  return (
    <MDBContainer className="pt-4 vh-100 text-left">
      {!isConnected ? <LoadingPage /> : <Controller event={event} />}
    </MDBContainer>
  )
}

export default App
