import React, { useState, useEffect, useRef } from "react"
import {
  MDBSpinner,
  MDBInputGroup,
  MDBBtn,
  MDBIcon,
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
} from "mdb-react-ui-kit"

export default function InputCard() {
  const [isLoading, setLoading] = useState(false)
  const tiktokIdRef = useRef(null)
  const btnRef = useRef(null)

  function start() {
    tiktokIdRef.current.setAttribute("disabled", true)
    btnRef.current.setAttribute("disabled", true)
    const id = tiktokIdRef.current.value
    window.socket.emit("createRoom", id)
    window.localStorage.setItem("lastTiktokId", id)
    setLoading(true)
  }

  useEffect(() => {
    return tiktokIdRef.current.focus()
  })

  useEffect(() => {
    window.socket.on("tiktok-connectSuccess", () => {
      window.toast.success(`Connected successfully!`)
      setLoading(false)
    })

    window.socket.on("tiktok-connectFailed", (error) => {
      tiktokIdRef.current.removeAttribute("disabled")
      btnRef.current.removeAttribute("disabled")
      window.toast.error(`Connect has failed: ${error}`)
      setLoading(false)
    })

    window.socket.on("tiktok-streamEnd", (reason) => {
      tiktokIdRef.current.removeAttribute("disabled")
      btnRef.current.removeAttribute("disabled")
      window.toast.info(`Livestream has ended: ${reason}`)
    })

    return () => {
      window.socket.off("tiktok-connectSuccess")
      window.socket.off("tiktok-connectFailed")
      window.socket.off("tiktok-streamEnd")
    }
  }, [])

  return (
    <MDBCard className="mb-3 rounded-7">
      <MDBCardHeader className="border-0">
        <h6>Tiktok livestream connect</h6>
      </MDBCardHeader>
      <MDBCardBody>
        {/* <h6 className="mb-4">Tiktok livestream connect</h6> */}
        <MDBInputGroup>
          <input
            ref={tiktokIdRef}
            className="form-control"
            placeholder="enter tiktok id"
            type="text"
            defaultValue={window.localStorage.lastTiktokId}
          />
          <MDBBtn ref={btnRef} onClick={start} className="position-relative">
            <span className={isLoading ? "invisible" : ""}>
              Connect <MDBIcon fas icon="angle-right" />
            </span>
            {isLoading && (
              <div className="position-absolute top-0 bottom-0 start-0 end-0 mx-auto">
                <MDBSpinner className="my-2" size="sm">
                  <span className="visually-hidden">Loading...</span>
                </MDBSpinner>
              </div>
            )}
          </MDBBtn>
        </MDBInputGroup>
      </MDBCardBody>
    </MDBCard>
  )
}
