import React, { useState, useRef } from "react"
import {
  MDBSpinner,
  MDBInputGroup,
  MDBBtn,
  MDBIcon,
  MDBCard,
  MDBCardHeader,
  MDBCardBody,
} from "mdb-react-ui-kit"

export default function InputCard() {
  const [isLoading, setLoading] = useState(false)
  const tiktokIdRef = useRef()
  const btnRef = useRef()
  function start() {
    console.log(tiktokIdRef.current.value)
  }
  return (
    <MDBCard className="mb-3">
      <MDBCardHeader>
        <h6>Tiktok livestream connect</h6>
      </MDBCardHeader>
      <MDBCardBody>
        <MDBInputGroup>
          <input
            ref={tiktokIdRef}
            className="form-control"
            placeholder="enter tiktok id"
            type="text"
            defaultValue={window.localStorage.lastTiktokId}
          />
          <MDBBtn ref={btnRef} onClick={start} className="position-relative">
            <span className={isLoading ? "invisible" : ""}>
              Connect <MDBIcon fas icon="angle-right" />
            </span>
            {isLoading && (
              <div className="position-absolute top-0 bottom-0 start-0 end-0 mx-auto">
                <MDBSpinner className="my-2" size="sm">
                  <span className="visually-hidden">Loading...</span>
                </MDBSpinner>
              </div>
            )}
          </MDBBtn>
        </MDBInputGroup>
      </MDBCardBody>
    </MDBCard>
  )
}
